mapper.insert:
insert into member
(
	memberId,
	memberPass,
	memberName,
	memberBirth,
	memberGender,
	memberTel,
	memberEmail,
	memberAddress,
	memberDate,
	memberPassHint,
	memberPassHintAnswer,
	postcode,
	address,
	detailAddress,
	extraAddress
)
values
(
	#{memberId}
	,#{memberPass}
	,#{memberName}
	,#{memberBirth}
	,#{memberGender}
	,#{memberTel}
	,#{memberEmail}
	,#{memberAddress}
	,sysdate
	,#{memberPassHint}
	,#{memberPassHintAnswer}
	,#{postcode}
	,#{address}
	,#{detailAddress}
	,#{extraAddress}
)
